import { createBrowserHistory } from "@remix-run/router";
import loading from "../assets/loading.png";
import { useState } from "react";
import { Link as OriginalLink } from "react-router-dom";

export default function SignUp() {
  const [email, setEmail] = useState("");
  const [pw, setPw] = useState("");
  const [passwordConfirm, setPasswordConfirm] = useState < string > "";

  const [emailValid, setEmailValid] = useState(false);
  const [pwValid, setPwValid] = useState(false);

  const handleEmail = (e) => {
    setEmail(e.target.value);
    const regex = /^[0-9]+$/;
    if (regex.test(email)) {
      setEmailValid(true);
    } else {
      setEmailValid(false);
    }
  };

  const handlePassWord = (e) => {
    setPw(e.target.value);
    const regex = /^[0-9]+$/;
    if (regex.test(pw)) {
      setPwValid(true);
    } else {
      setPwValid(false);
    }
  };

  return (
    <div className="enterInfo">
      <div className="signUpImage">
        <img src={loading} />
      </div>

      <div className="contentWrap">
        <div className="inputWrap">
          <div className="idinput">ID</div>
          <input
            className="input"
            type="text"
            value={email}
            onChange={handleEmail}
          />
        </div>

        <div className="errorMessageWrap">
          {!emailValid && email.length > 0 && (
            <div>올바른 이메일을 입력해주세요.</div>
          )}
        </div>

        <div className="inputWrap">
          <div className="PWinput">PW</div>
          <input
            className="input"
            type="password"
            value={pw}
            onChange={handlePassWord}
          />
        </div>

        <div className="errorMessageWrap">
          {!pwValid && pw.length > 0 && (
            <div> 영문, 숫자, 특수문자 포함 8자 이상 입력해주세요.</div>
          )}
        </div>

        <div className="inputWrap">
          <div className="PWconfirm">
            <input
              onChange={handlePassWord}
              passwordText=" "
              title="비밀번호 확인"
              typeTitle="passwordConfirm"
            />
          </div>
          {passwordConfirm.length > 0 && (
            <span
              className={`message ${isPasswordConfirm ? "success" : "error"}`}
            >
              {passwordConfirmMessage}
            </span>
          )}
        </div>

        <div>
          <OriginalLink to="/signup">
            <button className="bottomButton"> BACK </button>
          </OriginalLink>
          <OriginalLink to="/nextsignup">
            <button className="bottomButton2"> NEXT </button>
          </OriginalLink>
        </div>
      </div>
    </div>
  );
}
